/*
 * @name: 审查元素
 * @Author: Nekko
 * @version: 0.0.1
 * @description: 修改网页内容
 * @include: *
 */
var an=document.createElement("div");
an.innerHTML="审";
an.setAttribute("style",`font-size:5vw !important;
width:9vw !important;
height:9vw !important;
line-height:9vw !important;
text-align:center !important;
background-color:rgba(0,128,128,0.4) !important;
box-shadow:0px 0px 1px rgba(0,0,0,0) !important;
position:fixed !important;
bottom:47vh !important;
right:3vw !important;
z-index:99999 !important;
border-radius:100% !important;`);
an.onclick=function (){document.body.contentEditable='true'; document.designMode='on'; void 0};
document.getElementsByTagName("html").item(0).appendChild(an);
