/*
 * @name: 到顶到底
 * @Author: Sky
 * @version: 1.0.1
 * @description: 一键到达页面顶部/底部
 * @include: *
 * @createTime: 2020-9-22 13:00
 */
(function(){
 const key=encodeURIComponent('到顶到底:执行判断');
 if(window[key]){return;}
 try{
  window[key]=true;
  function s(i){window.scrollTo(0,i);}
  const f=document.createElement('link');
  f.rel='stylesheet';
  f.href="data:text/css,@font-face{font-family:TandB;src:url('data:font/woff2;base64,')}%23sky-scrolltop,%23sky-scrolltbtm{font-family:TandB!important;font-style:normal;font-weight:400;font-variant:normal;text-transform:none;-webkit-font-smoothing:antialiased;line-height:1;font-size:16px;padding:5px;background:%23222;border-radius:6px}";
  document.head.append(f);
  const d=document.createElement('div');
  d.innerHTML='<div id="sky-scrolltop">顶</div><div id="sky-scrolltbtm" style="margin-top:18px;">底</div>';
  d.style='position:fixed;left:15px;bottom:20%;z-index:999999;color:#eee;user-select:none;opacity:0.6;';
  document.body.append(d);
  document.getElementById('sky-scrolltop').onclick=()=>{s(0)};
  document.getElementById('sky-scrolltbtm').onclick=()=>{s(document.body.scrollHeight)};
 } catch(err){console.log('到顶到底：',err);}
})();
