import { runOnce } from '@bext/util';
import { bextButton } from '@bext/ui';

runOnce(() => {
    let scroller = (top, btn) => {
        document.querySelectorAll(':root,body,body *:not(aside,hr,br,wbr,button,input,progress,meter,select,option,optgroup,dialog,map,area)').forEach(bo => {
            if (window.innerHeight < bo.scrollHeight) {
                let toplen = top ? 0 : bo.scrollHeight;
                if (!bo.scrollTo) {
                    bo.scrollTop = toplen;
                } else {
                    bo.scrollTo({ top: toplen, behavior: 'smooth' });
                }
            }
        });
        btn.text = top ? '▲' : '▼';
    };
    let button = bextButton({
        text: '◆',
        btnColor: 'darkturquoise',
        textColor: 'white',
        posY: 'middle',
        drag: (btn, e) => {
            scroller.call(this, (e.changedTouches[0].clientY > (window.innerHeight / 2)), btn);
            setTimeout(btn => {
                btn.text = '◆';
                btn.posX = 'right';
                btn.posY = 'middle';
            }, 300, btn);
        }
    });
    button.push();
})
