import { runOnce, runAt } from '@bext/util';
import { getBextBar, toast } from '@bext/ui';
import config from '@bext/config';

runOnce(() => {
    if (!navigator.clipboard) return;

    let origExecCmd = document.execCommand,
        origClipWrite = navigator.clipboard.write,
        origClipWriteText = navigator.clipboard.writeText,
        copyCount = 0, copyStat = false, storKey = 'autoCopy_stat';

    if (window.localStorage.hasOwnProperty(storKey)) {
        if (config.storage) {
            config.enable = (window.localStorage.getItem(storKey)==0) ? true : false;
        } else {
            window.localStorage.removeItem(storKey);
        }
    }

    function addCount() {
        let bar = getBextBar();
        copyCount++;
        if (bar.query('autoCopy')) bar.change('autoCopy', {
            text: `限制自动复制 (${copyCount})`,
        });
    }
    function recordCopy(cmd,...arg){
        if (cmd.toLowerCase()=='copy') {
            addCount();
        } else {
            origExecCmd(cmd,...arg);
        }
    };
    function recordCopyPromise(){
        return new Promise(resolve => {
            addCount();
            resolve();
        });
    };
    function lockCopy(lock){
        document.execCommand = lock ? recordCopy : origExecCmd;
        navigator.clipboard.write = lock ? recordCopyPromise : origClipWrite;
        navigator.clipboard.writeText = lock ? recordCopyPromise : origClipWriteText;
        copyStat = lock ? false : true;
        copyCount = 0;
        if (config.storage) window.localStorage.setItem(storKey,lock ? 0 : 1);
    };
    lockCopy(config.enable);
    runAt('document-idle', function () {
        let text = ['允许自动复制', '限制自动复制'];
        getBextBar().add('autoCopy', {
            text: (config.enable) ? text[1] : text[0],
            textcolor: 'white',
            backcolor: (config.enable) ? 'crimson' : 'limegreen',
            callback: bar => {
                if (copyStat) {
                    bar.change('autoCopy', {
                        text: text[1],
                        backcolor: 'crimson'
                    });
                } else {
                    bar.change('autoCopy', {
                        text: text[0],
                        backcolor: 'limegreen'
                    });
                }
                lockCopy(copyStat);
                toast(copyStat ? text[0] : text[1]);
            },
        });
    })
})
