import { loadStyle } from '@bext/util';
import config from '@bext/config';
import 'https://cdn.staticfile.org/zepto/1.2.0/zepto.min.js';
import 'https://cdn.staticaly.com/gh/asteasolutions/material-refresh/master/material-refresh.min.js';

loadStyle(
    'https://cdn.staticaly.com/gh/asteasolutions/material-refresh/master/material-refresh.min.css',
    'fded1416f52de22357e4a65195523f3dd5d5035b3a643b210e9de600dafa0d31'
);
mRefresh({
    index: config.index,
    onBegin: () => location.reload(),
});
