/*
 * @name: Pure百度美化(ipad版)
 * @Author: 酷安搜CWorld
 * @version: 1.0
 * @description: 一个非常漂亮的百度质感美化皮肤
 * @include: *
 */

(function () {
  const whiteList = ["http://www.baidu.com/", "https://www.baidu.com/", "http://baidu.com/", "https://baidu.com/", "wenku.baidu.com", "zhidao.baidu.com", "passport.baidu.com", "i.baidu.com", "music.taihe.com", "play.taihe.com", "jingyan.baidu.com", "baike.baidu.com", "image.baidu.com", "site.baidu.com", "map.baidu.com", "nuomi.com", "pan.baidu.com", "yun.baidu.com", "duanxin.baidu.com", "wenzhang.baidu.com", "tongxunlu.baidu.com", "note.baidu.com", "tonghuajilu.baidu.com", "fanyi.baidu.com", "xueshu.baidu.com", "bce.baidu.com", "video.baidu.com", "news.baidu.com", "baijiahao.baidu.com", "mbd.baidu.com", "top.baidu.com", "cache.baiducontent.com", "dwz.cn"];
  const hostname = window.location.hostname;
  const key = encodeURIComponent('酷安搜CWorld:Pure百度美化(for ipad版):执行判断');
  const canLoad = whiteList.some(url => {
    if (url.match(hostname)) {
      return true;
    };
    return false;
  });

  if (!canLoad || window[key]) {
    return;
  };
  window[key] = true;

  class BeautifyBaidu {
    constructor() {
      this.init()
    };
    init() {
      const that = this;
      if (that.timer) {
        clearInterval(that.timer);
      };
      that.addScript(() => {
        that.timer = setInterval(() => {
          const styleElm = document.head.querySelector('#pure-baidu-pc-css');
          console.log(styleElm);
          if (!styleElm) {
            that.init();
          };
        }, 500);
      });
    };
    addScript(callback) {
      const script = document.createElement('script');
      script.id = 'pure-baidu-pc';
      script.src = 'https://cdn.jsdelivr.net/npm/via-scripts@2.0.0/%E7%BE%8E%E5%8C%96%E7%B1%BB/pure%E7%99%BE%E5%BA%A6%E7%BE%8E%E5%8C%96(ipad%E7%89%88)/remote.js';
      script.onload = callback();
      document.body.appendChild(script);
    };
    removeScript() {

    };
  };
  try {
    new BeautifyBaidu();
  } catch (err) {
    console.log('via插件：Pure百度美化(ipad版)：加载失败', err);
  };
})();
