import { runOnce, runAt } from '@bext/util';
import { bextButton } from '@bext/ui';

runOnce(() => {
    runAt('document-idle', () => {
        let bodys = document.querySelectorAll(':root,body,body *:not(aside,hr,br,wbr,button,input,progress,meter,select,option,optgroup,dialog,map,area)'),
            scroller = (top,btn) => {
                bodys.forEach(bo => {
                    let bost = getComputedStyle(bo);
                    if (window.innerHeight < bo.scrollHeight) {
                        let toplen = top ? 0 : parseInt(bost.height);
                        if (!bo.scrollTo) {
                            bo.scrollTop = toplen;
                        } else {
                            bo.scrollTo({ top: toplen, behavior: 'smooth' });
                        }
                    }
                });
                btn.text = top ? '▲' : '▼';
            };
        bextButton({
            text: '◆',
            btnColor: 'darkturquoise',
            textColor: 'white',
            posY: 'middle',
            drag: (btn,e) => {
                scroller.call(this,(e.changedTouches[0].clientY > (window.innerHeight / 2)),btn);
                setTimeout(btn => {
                    btn.text = '◆';
                    btn.posX = 'right';
                    btn.posY = 'middle';
                },300,btn);
            }
        })
    })
})
