(function () {
  'use strict';

  const id = decodeURIComponent('98778');

  function runOnce(fn) {
    const uniqId = 'BEXT_UNIQ_ID_' + id;
    if (window[uniqId]) {
      return;
    }
    window[uniqId] = true;
    fn && fn();
  }


  function runAt(start, fn, ...args) {
    if (typeof fn !== 'function') return;
    switch (start) {
      case 'document-body':
        document.addEventListener('readystatechange', function () {
          if (document.readyState === 'interactive') fn.call(this, ...args);
        });
        break;
      case 'document-end':
        document.addEventListener('DOMContentLoaded', fn.bind(this, ...args));
        break;
      case 'document-idle':
        window.addEventListener('load', fn.bind(this, ...args));
        break;
      default:
        if (typeof start == 'number') {
          setTimeout(fn, start, ...args);
        } else {
          window.addEventListener('load', function () {
            setTimeout(fn, parseInt(start), ...args);
          });
        }
    }
  }

  function addElement({ tag, attrs = {}, to = document.body }) {
    const el = document.createElement(tag);
    Object.assign(el, attrs);
    to.appendChild(el);
    return el;
  }

  function addStyle(css) {
    return addElement({
      tag: 'style',
      attrs: {
        textContent: css,
      },
      to: document.head,
    });
  }


  function getBextBar() {
    if (!document.querySelector('#bextBar')) {
      let enableFreeFeature = false,
        bar,
        buttons = [],
        pos = [],
        downPos = 0,
        clickmove = 50,
        first,
        unlock = () => (document.onmousemove = null),
        isObj = (o) =>
          typeof o == 'object' && !(o instanceof Array || o instanceof Function),
        optCheck = (opt, strict) => {
          if (
            !isObj(opt) ||
            (!opt.backcolor && !opt.textcolor && !opt.text && !opt.callback)
          )
            return 2;
          if (
            (strict || opt.text) &&
            (typeof opt.text !== 'string' || opt.text == '')
          )
            return 3;
          if ((strict || opt.callback) && typeof opt.callback !== 'function')
            return 4;
          if (
            opt.backcolor &&
            (typeof opt.backcolor !== 'string' || opt.backcolor == '')
          )
            return 5;
          if (
            opt.textcolor &&
            (typeof opt.textcolor !== 'string' || opt.textcolor == '')
          )
            return 6;
          return 0;
        };

      addStyle(`
        #bextBar {
            position: fixed;
            display: block;
            max-width: 80vw;
            height: max-content;
            top: calc(95vh - 110px);
            margin: 5px;
            padding: 2px;
            background: white;
            border-radius: 5px;
            opacity: 0.7;
            z-index: 1000000;
            box-shadow: 0 2px 5px gray;
            user-select: none;
        }
        #bextBar.close {
            width: 23px;
            height: 33px;
            overflow: clip;
        }
        #bextBarExpand {
            margin: 10px;
            margin-right: 0;
            padding: 0;
            border-width: 4px 0 0 4px;
            border-color: #333 transparent transparent #333;
            border-style: solid none none solid;
            width: 10px;
            height: 10px;
            transform: rotate(315deg);
            float: left;
        }
        #bextBar.close #bextBarExpand {
            transform: rotate(135deg);
            margin-left: 3px;
        }
        .bextButton {
            margin: 5px .5em;
            padding: 4px .75em;
            background: whitesmoke;
            color: #333;
            border: none;
            outline: none;
            border-radius: 1em;
            font-size: 13px;
            line-height: 13px;
            width: auto;
            box-shadow: 0 2px 2px lightgray;
            transition: opacity .5s, background-color .25s, color .25s;
        }
        .bextButton span {
            margin: .125rem;
            display: inline;
        }
        .delButton {
            font-family: sans-serif;
        }
        .ghost {
            opacity: 0.5;
        }
        #bextGhostBox {
            position: fixed;
            left: -150%;
            top: -150%;
            background: transparent;
            z-index: 1000001;
        }
        #bextGhost {
            position: absolute;
            top: 0;
            left: 0;
            width: max-content;
            opacity: 2;
        }
        @media (prefers-color-scheme: dark) {
            #bextBar {
                background: #283344;
                box-shadow: 0 0 2px #3b81ff;
            }
            #bextBarExpand {
                border-color: #3b81ff transparent transparent #3b81ff;
            }
            .bextButton {
                background: #4c5161;
                color: #ddf;
                box-shadow: 0 0 5px #3b81ff;
            }
        }
        `);
      bar = addElement({
        tag: 'div',
        attrs: {
          id: 'bextBar',
          className: 'close',
        },
      });
      first = addElement({
        tag: 'div',
        attrs: {
          id: 'bextBarExpand',
          onclick: function (e) {
            if (
              'ontouchend' in document ||
              Math.abs(downPos - e.clientX * e.clientY) <= clickmove
            )
              bar.classList.toggle('close');
          },
        },
        to: bar,
      });


      if ('ontouchend' in document) {
        first.addEventListener(
          'touchstart',
          function (e) {
            pos = [
              e.touches[0].clientX - bar.offsetLeft,
              e.touches[0].clientY - bar.offsetTop,
            ];
          },
          { passive: true },
        );
        first.addEventListener(
          'touchmove',
          function (e) {
            bar.style.left =
              Math.max(
                0,
                Math.min(e.touches[0].clientX - pos[0], window.innerWidth - 27),
              ) + 'px';
            bar.style.top =
              Math.max(
                0,
                Math.min(e.touches[0].clientY - pos[1], window.innerHeight - 37),
              ) + 'px';
          },
          { passive: true },
        );
      } else {
        first.addEventListener('mousedown', function (e) {
          pos = [
            Math.max(0, e.clientX - bar.offsetLeft),
            Math.max(0, e.clientY - bar.offsetTop),
          ];
          downPos = e.clientX * e.clientY;
          document.onmousemove = function (e) {
            if (Math.abs(downPos - e.clientX * e.clientY) > clickmove) {
              bar.style.left =
                Math.max(
                  0,
                  Math.min(e.clientX - pos[0], window.innerWidth - 27),
                ) + 'px';
              bar.style.top =
                Math.max(
                  0,
                  Math.min(e.clientY - pos[1], window.innerHeight - 37),
                ) + 'px';
            }
          };
          document.removeEventListener('mouseup', unlock);
          document.addEventListener('mouseup', unlock);
        });
      }

      Object.assign(bar, {
        queryAll: function () {
          return buttons;
        },
        del: function (id) {
          if (!buttons.includes(id)) return 1;
          let button = this.querySelector(`#bextButton-${id}`);
          if (button) button.remove();
          buttons.splice(buttons.indexOf(id), 1);
          return 0;
        },
        query: function (id) {
          if (!buttons.includes(id)) return 1;
          let button = this.querySelector(`#bextButton-${id}`);
          if (!button) {
            buttons.splice(buttons.indexOf(id), 1);
            return 8;
          }
          return {
            button: button,
            text: button.querySelector('span').innerText,
            backcolor: button.style.backgroundColor,
            textcolor: button.style.color,
          };
        },
        change: function (id, opt) {
          if (!buttons.includes(id)) return 1;
          let optret = optCheck(opt, false);
          if (optret !== 0) return optret;
          let button = this.querySelector(`#bextButton-${id}`);
          if (button) {
            if (opt.textcolor) button.style.color = opt.textcolor;
            if (opt.backcolor) button.style.backgroundColor = opt.backcolor;
            if (opt.text) button.querySelector('span').innerText = opt.text;
            if (opt.callback)
              button.onclick = opt.callback.bind(window, this, button);
            return 0;
          } else {
            buttons.splice(buttons.indexOf(id), 1);
            return 8;
          }
        },
        add: function (id, opt) {
          if (!id || !isObj(opt)) return 1;
          opt = Object.assign(
            {
              text: '按钮',
            },
            opt,
          );
          if (typeof id !== 'string') return 7;
          let optret = optCheck(opt, true);
          if (optret !== 0) return optret;
          if (!this.querySelector(`#bextButton-${id}`)) {
            let button = addElement({
                tag: 'button',
                attrs: {
                  id: `bextButton-${id}`,
                  className: 'bextButton',
                  draggable: enableFreeFeature,
                },
                to: this,
              });
            addElement({
              tag: 'span',
              to: button,
            });
            buttons.push(id);
            let change = this.change(id, opt);
            if (change != 0) this.del(id);
            return change;
          } else return 10;
        },
      });
    }
    return document.querySelector('#bextBar');
  }

  function copyMain(opt){
      let origExecCmd = document.execCommand,
          origClipWrite = navigator.clipboard.write,
          origClipWriteText = navigator.clipboard.writeText,
          copyCount = 0, copyStat = false;

      if (window.localStorage.hasOwnProperty('autoCopy_stat')) {
          opt.默认开启限制 = (window.localStorage.getItem('autoCopy_stat')==0) ? true : false;
      }

      function addCount() {
          let bar = getBextBar();
          copyCount++;
          if (bar.query('autoCopy')) bar.change('autoCopy', {
              text: `限制自动复制 (${copyCount})`,
          });
      }
      function recordCopy(cmd,...arg){
          if (cmd=='copy') {
              addCount();
          } else {
              origExecCmd(cmd,...arg);
          }
      }    function recordCopyPromise(){
          return new Promise(resolve => {
              addCount();
              resolve();
          });
      }    function lockCopy(lock){
          document.execCommand = lock ? recordCopy : origExecCmd;
          navigator.clipboard.write = lock ? recordCopyPromise : origClipWrite;
          navigator.clipboard.writeText = lock ? recordCopyPromise : origClipWriteText;
          copyStat = lock ? false : true;
          copyCount = 0;
          window.localStorage.setItem('autoCopy_stat',lock ? 0 : 1);
      }    lockCopy(opt.默认开启限制);
      runAt('document-idle', function () {
          let text = ['允许自动复制', '限制自动复制'];
          getBextBar().add('autoCopy', {
              text: (opt.默认开启限制) ? text[1] : text[0],
              textcolor: 'white',
              backcolor: (opt.默认开启限制) ? 'crimson' : 'limegreen',
              callback: (bar) => {
                  if (copyStat == true) {
                      lockCopy(true);
                      bar.change('autoCopy', {
                          text: text[1],
                          backcolor: 'crimson'
                      });
                  } else {
                      lockCopy(false);
                      bar.change('autoCopy', {
                          text: text[0],
                          backcolor: 'limegreen'
                      });
                  }
              },
          });
      });
  }

  runOnce(() => {
      copyMain({
          默认开启限制: !!1
      });
  });

})();
