import { loadStyle } from '@bext/util';
import config from '@bext/config';
import 'https://cdn.staticfile.org/zepto/1.2.0/zepto.min.js';
import 'https://cdn.staticaly.com/gh/abseralvo/material-refresh/no-deps/material-refresh.min.js';

loadStyle(
    'https://cdn.staticaly.com/gh/abseralvo/material-refresh/no-deps/material-refresh.min.css',
    '16e2b7d60ddf08e40dc3590589434c0ed082ad0ac7470b8790063baf394bad10'
);
mRefresh({
    maxTime: config.maxTime,
    index: config.index,
    onEnd: () => location.reload(),
});
